import React from 'react';
import { AlphaPicker, SketchPicker } from 'react-color';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      alphaColor: {
        r: '255',
        g: '255',
        b: '255',
        a: '1',
      },
      sketchColor: {
        r: '255',
        g: '255',
        b: '255',
        a: '1',
      }
    };
  }

  handleAlphaChange = (color) => {
    console.log(color)
    let newColor = {
      r: "206",
      g: "148",
      b: "35",
      a: color.rgb.a
    }
    this.setState({
     alphaColor: newColor
    })
 }

  handleSketchChange = (color) => {
     this.setState({
      sketchColor: color.rgb
     })
  }

  render() {
    return (
      <div className="container">
        <div 
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between"
          }}
        >
          <SketchPicker
            color={ this.state.sketchColor }
            onChange={ this.handleSketchChange }
          />
          <div 
            style={{
              backgroundColor: `rgba(${ this.state.sketchColor.r }, ${ this.state.sketchColor.g }, ${ this.state.sketchColor.b }, ${ this.state.sketchColor.a })`,
              borderRadius: "50%",
              width: "200px",
              height: "200px",
              marginRight: "50px"
            }} 
          />
        </div>

        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between"
          }}
        >
          <AlphaPicker
              color={ this.state.alphaColor }
              onChange={ this.handleAlphaChange }
          />
          <div 
            style={{
              backgroundColor: `rgba(${ this.state.alphaColor.r }, ${ this.state.alphaColor.g }, ${ this.state.alphaColor.b }, ${ this.state.alphaColor.a })`,
              borderRadius: "50%",
              width: "200px",
              height: "200px",
              marginRight: "50px"
            }}
          />
        </div>
      </div>
    );
  }
}

 export default App;
