import React from 'react';
import { SketchPicker } from 'react-color';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      background: {
        r: "",
        g: "",
        b: "",
        a: ""
      }
    };
  }


  handleChange = (color) => {
    console.log("handlechange", color)
     this.setState({
       background: color.rgb
     })
  }



  render() {
    return (
      <SketchPicker
        color={ this.state.background.hex }
        onChange={ this.handleChange }
      />
    );
  }
}

 export default App;
