import React from 'react';
import { SketchPicker } from 'react-color';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      color: {
        r: '255',
        g: '255',
        b: '255',
        a: '1',
      }
    };
  }


  handleChange = (color) => {
     this.setState({
       color: color.rgb
     })
     console.log(this.state)
  }



  render() {
    return (
      <div style={{backgroundColor: `rgba(${ this.state.color.r }, ${ this.state.color.g }, ${ this.state.color.b }, ${ this.state.color.a })`}}>
        <SketchPicker
          color={ this.state.color }
          onChange={ this.handleChange }
        />
      </div>
    );
  }
}

 export default App;
