import React from 'react';
import { AlphaPicker, SketchPicker } from 'react-color';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      alphaColor: {
        r: '255',
        g: '255',
        b: '255',
        a: '1',
      },
      sketchColor: {
        r: '255',
        g: '255',
        b: '255',
        a: '1',
      }
    };
  }

  handleAlphaChange = (color) => {
    console.log(color)
    this.setState({
     alphaColor: color.rgb
    })
 }

  handleSketchChange = (color) => {
    let newColor = {
      r: "206",
      g: "148",
      b: "35",
      a: color.rgb.a
    }
     this.setState({
      sketchColor: newColor
     })
  }

  render() {
    return (
      <div className="container">
        <div style={{backgroundColor: `rgba(${ this.state.sketchColor.r }, ${ this.state.sketchColor.g }, ${ this.state.sketchColor.b }, ${ this.state.sketchColor.a })`}}>
          <SketchPicker
            color={ this.state.sketchColor }
            onChange={ this.handleChange }
          />
        </div>
        <div>
          <AlphaPicker
            color={ this.state.alphaColor }
            onChange={ this.handleAlphaChange }
          />
        </div>
      </div>
    );
  }
}

 export default App;
